import React from 'react';

class Items extends React.Component {
    constructor(props) {
        super(props);
        this.state = {list: []};
    }

    componentDidMount() {
        this.getData()
    }

    getData() {
        fetch('http://localhost:8080/instructors/tharms/courses', {
            method: 'GET',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => {
            return response.json()
        }).then(json => {
            this.setState({
                list:json
            });
        });
    }

    handleSubmit(event) {
        event.preventDefault();
        const data = new FormData(event.target);

        console.log(data.username);

        fetch('http://localhost:8080/instructors/tharms/courses', {
            method: 'POST',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            },
            body: JSON.stringify(data),
        }).then(res => {
            this.getData();
        }).catch(err => err);
    }

    render() {
        return (
            <div>
                <form onSubmit={this.handleSubmit()}>
                    <label htmlFor="id">Enter your id</label>
                    <input id="id" name="id" type="number" />
                    <br/>
                    <label htmlFor="username">Enter username</label>
                    <input id="username" name="username" type="text" />
                    <br/>
                    <label htmlFor="description">Enter your description</label>
                    <input id="description" name="description" type="text" />
                    <br/>
                    <button>Send data!</button>
                </form>

                <p><b>New Resource created in the server as shown below</b></p>
                <ul>
                    {this.state.list.map(d => <li key={d.id}>[{d.id}] - {d.username} - {d.description}</li>)}
                </ul>
            </div>
        )
    }
}

export default Items;
