import React from 'react';
import ItemDataService from './ItemDataService';

class Items extends React.Component {
    constructor(props) {
        super(props);
        this.state = {list: []};
    }

    componentDidMount() {
        this.getData()
    }

    getData() {
        ItemDataService.retrieveAllCourses("tharms")//HARDCODED
            .then(
                response => {
                    console.log(response);
                    this.setState({list: response.data})
                }
            )
    }

    handleSubmit(event) {
        event.preventDefault();
        const data = new FormData(event.target);

        console.log(data.username);

        fetch('http://localhost:8080/instructors/tharms/courses', {
            method: 'POST',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            },
            body: JSON.stringify(data),
        }).then(res => {
            this.getData();
        }).catch(err => err);
    }

    deleteCourseClicked(id) {
        ItemDataService.deleteCourse(id)
            .then(
                response => {
                    this.setState({ message: `Delete of course ${id} Successful` })
                    this.getData()
                }
            )
    }

    render() {
        return (
            <div>
                <form onSubmit={() => this.handleSubmit()}>
                    <label htmlFor="id">Enter your id</label>
                    <input id="id" name="id" type="number" />
                    <br/>
                    <label htmlFor="username">Enter username</label>
                    <input id="username" name="username" type="text" />
                    <br/>
                    <label htmlFor="description">Enter your description</label>
                    <input id="description" name="description" type="text" />
                    <br/>
                    <button>Send data!</button>
                </form>

                <h3>All Courses</h3>
                <div className="container">
                    <table className="table">
                        <thead>
                        <tr>
                            <th>Id</th>
                            <th>Description</th>
                        </tr>
                        </thead>
                        <tbody>
                        {
                            this.state.list.map(
                                item =>
                                    <tr key={item.id}>
                                        <td>{item.id}</td>
                                        <td>{item.description}</td>
                                        <td><button className="btn btn-warning" onClick={() => this.deleteCourseClicked(item.id)}>Delete</button></td>
                                    </tr>
                            )
                        }
                        </tbody>
                    </table>
                </div>
            </div>
        )
    }
}

export default Items;
