class ItemDataService {

    retrieveAllItems(name) {
        return fetch(`http://localhost:8080/groups/tharms${name}/courses`, {
            method: 'GET',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(response => {
            return response.json();
        });
    }

    deleteCourse(name, id) {
        //console.log('executed service')
        return fetch(`http://localhost:8080/groups/${name}/courses/${id}`, {
            method: 'DELETE',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            }
        }).then(() => {
            return 'OK';
        });
    }

    addCourse(item) {
        return fetch(`http://localhost:8080/groups/${item.username}/courses`, {
            method: 'POST',
            headers: {
                "Content-type": "application/json; charset=UTF-8"
            },
            body: JSON.stringify(item),
        }).then(() => {
            return 'OK';
        });
    }

}

export default new ItemDataService()
