import React, { Component } from 'react';
import GoogleAnalytics from 'react-ga';
import PropTypes from 'prop-types';

const IS_PRODUCTION = process.env.NODE_ENV === 'production';

if (IS_PRODUCTION) {
  GoogleAnalytics.initialize('UA-111065371-1');
}

function getDisplayName(reactComponent) {
  return reactComponent.displayName || reactComponent.name || 'Component';
}

const withTracker = (WrappedComponent, options = {}) => {
  class WithTracker extends Component {
    static propTypes = {
      location: PropTypes.object.isRequired,
    };

    componentDidMount() {
      if (!IS_PRODUCTION) return;

      const page = this.props.location.pathname;
      this.trackPage(page);
    }

    componentDidUpdate(prevProps) {
      if (!IS_PRODUCTION) return;

      const currentPage = this.props.location.pathname;
      const prevPage = prevProps.location.pathname;
      if (currentPage !== prevPage) {
        this.trackPage(currentPage);
      }
    }

    trackPage = page => {
      GoogleAnalytics.set({
        page,
        ...options,
      });
      GoogleAnalytics.pageview(page);
    };

    render() {
      return <WrappedComponent {...this.props} />;
    }
  }

  WithTracker.displayName = `WithTracker(${getDisplayName(WrappedComponent)})`;
  return WithTracker;
};

export default withTracker;
